mapper.update:
UPDATE Vehicle v
SET v.allocation = v.allocation + (
    SELECT COALESCE(CEIL(((((p.height / 1000) * (p.width / 1000) * (p.depth / 1000)) / v.volume) * 100) * 100) / 100, 0)

FROM Dispatch d
             LEFT JOIN Outbound o ON o.id = #{outboundId}
             LEFT JOIN Product p ON p.id = o.product_id
    WHERE #{vehicleId} = v.id
    LIMIT 1
)
WHERE v.id IN (
    SELECT d.vehicle_id
    FROM Dispatch d
);

mapper.insert:
INSERT INTO DispatchApproval (dispatch_id, status, created_at)
VALUES (
        (SELECT d.id
        FROM Dispatch d
        LEFT JOIN Outbound o ON o.id = d.outbound_id
        WHERE o.id = #{outboundId}
        LIMIT 1),

        #{status},
        #{createdAt}
        );
