mapper.select:
SELECT i.id , i.user_id AS userId , u.name AS userName , p.id AS productId , p.name AS productName , i.cell_id AS cellId , i.expected_date AS expectedDate , i.quantity , ia.status , i.created_at AS createdAt , ia.created_at AS approvedAt FROM Inbound i JOIN User u ON u.id = i.user_id JOIN Product p ON p.id = i.product_id JOIN InboundApproval ia ON ia.inbound_id = i.id JOIN (SELECT c.id AS cellId , w.id AS warehouseId FROM Cell c JOIN Section s ON s.id = c.section_id JOIN Warehouse w ON w.id = s.warehouse_id) wsc ON wsc.cellId = i.cell_id JOIN (SELECT MAX(created_at) AS createdAt , inbound_id FROM InboundApproval GROUP BY inbound_id) maxCrt ON maxCrt.inbound_id = ia.inbound_id AND maxCrt.createdAt = ia.created_at
AND ia.status = #{search.S}
AND wsc.warehouseId = #{search.WH}
AND i.expected_date BETWEEN #{search.SD} AND #{search.ED}
ORDER BY ${orderBy} ${orderByDir}
LIMIT #{size} OFFSET #{offset};
