mapper.select:
SELECT o.id, p.name AS productName, o.quantity, u.company_name AS companyName, o.is_dispatch AS isDispatch, o.is_invoice AS isInvoice, oa.status AS approvalStatus FROM Outbound o
LEFT JOIN Cell c ON o.cell_id = c.id LEFT JOIN Section s ON c.section_id = s.id LEFT JOIN Warehouse w ON s.warehouse_id = w.id
LEFT JOIN OutboundApproval oa ON o.id = oa.outbound_id LEFT JOIN Product p ON p.id = o.product_id LEFT JOIN User u ON u.id = o.user_id LEFT JOIN Dispatch d ON d.outbound_id = o.id LEFT JOIN DispatchApproval da ON d.id = da.dispatch_id
AND w.name = #{search.WH}
AND oa.status = #{search.S}
AND da.status = #{search.DS}
AND o.created_at BETWEEN #{search.SD} AND #{search.ED}
LIMIT #{size} OFFSET #{offset};
