mapper.select:
SELECT u.id , u.name , u.company_name AS companyName , u.email , u.business_number AS businessNumber , u.phone , ua.status FROM User u JOIN UserApproval ua ON ua.user_id = u.id JOIN (SELECT MAX(created_at) created_at , user_id FROM UserApproval GROUP BY user_id) maxCrt ON maxCrt.user_id = ua.user_id AND maxCrt.created_at = ua.created_at
AND ua.status = #{search.S}
AND u.company_name LIKE '%${search.N}%'
AND u.email LIKE '%${search.E}%'
ORDER BY ${orderBy} ${orderByDir}
LIMIT #{size} OFFSET #{offset}

SELECT COUNT(id)
FROM UserApproval
WHERE user_id = #{userId}
